onContextMenu.body:
const _this = this; /* Use _this on async callbacks */
const selectedNode = event.node
const nodes = _this.components.menu_tree.nodes[0].children
debugger;

/**
 * Convert list to metadata format
 */
const treeToList = function (_node) {
    return _node.map((_leave) => {
        let _item = {
            key: _leave.key,
            label: _leave.label,
            icon: _leave.icon,
            command:  _leave.command,
        }
        if (_leave.children) {
            _item.components = treeToList(_leave.children)
        }
        return _item
    })
}

/**
 * Call backend for update menu
 */
const updateMenu = (_node) => {
    const body = {
        menu: treeToList(_node)
    }

    return utils.callServer({
        url: '/menu',
        method: 'patch',
        params: {
            application: _this.context.destApplication
        },
        data: body,
    }).then(function (response) {
        if (response.data.success == true) {
            utils.showToast({
                severity: 'success',
                summary: 'Menu updated',
                detail: 'ðŸ˜',
                life: 3000,
            })
        }

        return Promise.resolve(response)
    })
}

/**
 * Ask for delete a leave
 */
const deleteLeave = (isDir) => {
    utils.showConfirmDialog({
        message: isDir ? 'Do you want to delete this folder?' : 'Do you want to delete this leave?',
        header: 'Delete Confirmation',
        icon: 'pi pi-info-circle',
        acceptClassName: 'p-button-danger',
        accept: function () {

            _this.components.leave_grid_container.setIsToolbarLoading(true)

            updateMenu(nodes.filter((_leave) => { return _leave !== selectedNode }))
                .then(function (response) {
                    if (response.data.success == true) {
                        _this.components.leave_code.setRecord({})
                        _this.components.leave_info.setRecord({})
                        _this.components.menu_tree.load()
                    } else {
                        utils.showToast({
                            severity: 'error',
                            summary: 'Error',
                            detail: JSON.stringify(response.data),
                            sticky: true,
                        })
                    }

                    _this.components.leave_grid_container.setIsToolbarLoading(false)
                }).catch(function (error) {
                    utils.showToast({
                        severity: 'error',
                        summary: 'Error',
                        detail: 'Servererror: ' + error || 'Error',
                        sticky: true,
                    })

                    _this.components.leave_grid_container.setIsToolbarLoading(false)
                })
        },
        reject: function () { },
    })
}

/**
 * Ask for create leave 
 */
const createLeave = (isDir) => {

    utils.showInputDialog({
        header: isDir ? 'New folder' : 'New leave',
        message: isDir ? 'A folder cannot be empty, please specify the folder name' : 'Please specify a leave name',
        defaultValue: '',
        validate: (_event, _context) => {
            return {
                success: true
            }
        },
        accept: (_event, _context) => {
            _this.components.leave_grid_container.setIsToolbarLoading(true)

            /**
             * Create an empty scripts
             */
            const scriptSource = `/**\n * @author:${utils.userCredential.username}\n * @date: ${new Date().toGMTString()}\n */`
            const newLeave = {
                key: `${Date.now()}`,
                label: _event.value,
                icon: `pi pi-fw ${isDir ? 'pi-folder' : 'pi-file'}`,
                command: {
                    body: scriptSource
                },
            }

            if (isDir) {
                newLeave.children = []
            }

            selectedNode.children.push(newLeave) //<-- error!!!


            updateMenu(nodes)
                .then(function (response) {
                    if (response.data.success !== true) {
                        utils.showToast({
                            severity: 'error',
                            summary: 'Error',
                            detail: JSON.stringify(response.data),
                            sticky: true,
                        })
                    }
                    _this.components.leave_grid_container.setIsToolbarLoading(false)

                }).catch(function (error) {
                    utils.showToast({
                        severity: 'error',
                        summary: 'Error',
                        detail: 'Server error: ' + error || 'Error',
                        sticky: true,
                    })
                    _this.components.leave_grid_container.setIsToolbarLoading(false)
                })
        },
    })
}



/**************************************
 * Add buttons based on selected leave
 **************************************/

let buttons = []
buttons.push({
    label: 'New folder',
    icon: 'pi pi-folder',
    command: () => {
        createLeave(true)
    },
})
buttons.push({
    label: 'New leave',
    icon: 'pi pi-file',
    command: () => {
        createLeave(false)
    },
})

if (event.node.isRoot || event.node.children) {
    //can delete folder only if not root
    if (!event.node.isRoot) {
        buttons.push({
            label: 'Delete folder',
            icon: 'pi pi-trash',
            command: () => {
                deleteLeave(true)
            },
        })
    }
} else {
    //can delete file
    buttons.push({
        label: 'Delete leave',
        icon: 'pi pi-trash',
        command: (event) => {
            deleteLeave(false)
        },
    })
}

return buttons

onSelect.body:
const _this = this; /* Use _this on async callbacks */
_this.components.leave_grid_container.setShowToolbar(false);
_this.components.leave_grid_container.setTitle(event.node?.label || '');
_this.components.label.setDisabled(true);
_this.components.icon.setDisabled(true);
_this.components.source.setDisabled(true);
if (!event.node.command) {
    event.node.command = '';
} 

const record = {
    'key': event.node.key,
    'source': event.node.command.body?.trim(),
    'label': event.node.label,
    'icon': event.node.icon
};

_this.components.leave_info.setRecord(record);
_this.components.leave_code.setRecord(record);
_this.components.label.setDisabled(false);
_this.components.icon.setDisabled(false);
_this.components.source.setDisabled(false);
_this.components.leave_grid_container.setShowToolbar(true);

onClick.body:
const _this = this; /* Use _this on async callbacks */
_this.components.leave_grid_container.setIsToolbarLoading(true);

debugger;

const record = {
    key: _this.components.leave_info.getRecord().key,
    label: _this.components.leave_info.getRecord().label,
    icon: _this.components.leave_info.getRecord().icon,
    command: {
        body: _this.components.leave_code.getRecord().source
    }
};


const nodes = _this.components.menu_tree.nodes[0].children;


const treeToList = function (_node) {
    return _node.map(_leave => {
        if (_leave.key === record.key) {
            _leave = {
                ..._leave,
                ...record
            };
        }
        if (_leave.children) {
            _leave.components = treeToList(_leave.children);
            delete _leave.children
        };
        return _leave;
    })
};


const body = {
    'menu': treeToList(nodes)
};
utils.callServer({
    'url': '/menu',
    'method': 'patch',
    'params': {
        'application': _this.context.destApplication,
    },
    'data': body
}).then(function (response) {
    if (response.data.success == true) {
        utils.showToast({
            'severity': 'success',
            'summary': 'Script updated',
            'detail': JSON.stringify(response.data),
            'life': 3000
        });
        _this.components.menu_tree.load();
        _this.components.menu_tree.setNodes(_this.components.menu_tree.nodes);
    } else {
        utils.showToast({
            'severity': 'error',
            'summary': 'Error',
            'detail': JSON.stringify(response.data),
            'sticky': true
        });
    };
    _this.components.leave_grid_container.setIsToolbarLoading(false);
}).catch(function (error) {
    utils.showToast({
        'severity': 'error',
        'summary': 'Error',
        'detail': 'Server error: ' + error || 'Error',
        'sticky': true
    });
    _this.components.leave_grid_container.setIsToolbarLoading(false);
});
